#!/usr/bin/env python3

nt_count={} #create an empty dictionary to storte information from the iteration completed on the dna strand.
dna = ''

for nt in dna:
    if nt in nt_count:
        previous_count = nt_count[nt]
        new_count = previous_count + 1
        nt_count[nt] = new_count
    else:
        nt_count[nt] = 1
print(nt_count)

ATGC = (nt_count['A'] + nt_count['T'] + nt_count['G'] + nt_count['C'])

GC = (nt_count['G'] + nt_count['C'])/ATGC

print(f'The GC content of this DNA sequence is: {GC:.2%}')